// src/components/Vote.js
import React, { useState } from 'react';
import axios from 'axios';

function Vote({ match }) {
  const [selectedOption, setSelectedOption] = useState('');
  const pollId = match.params.pollId;

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send vote to Django backend
    axios.post(`http://localhost:8000/api/polls/${pollId}/vote/`, { selectedOption })
      .then(response => {
        console.log(response.data);
        // Redirect to results or handle success
      })
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h2>Cast Your Vote</h2>
      <form onSubmit={handleSubmit}>
        <div className="radio">
          <label>
            <input
              type="radio"
              name="poll"
              value="option1"
              checked={selectedOption === 'option1'}
              onChange={() => setSelectedOption('option1')}
            />
            Option 1
          </label>
          {/* Similar radio inputs for option2 and option3 */}
        </div>

        <button type="submit" className="btn btn-info">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Vote;
