// src/components/Create.js
import React, { useState } from 'react';
import axios from 'axios';

function Create() {
  const [formData, setFormData] = useState({
    question: '',
    option_one: '',
    option_two: '',
    option_three: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send data to Django backend
    axios.post('http://localhost:8000/api/polls/', formData)
      .then(response => {
        console.log(response.data);
        // Redirect to home or handle success
      })
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h1>Create A New Poll</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="question">Enter Poll Question</label>
        <textarea
          name="question"
          value={formData.question}
          onChange={handleChange}
          rows="3"
          className="form-control"
        ></textarea>

        {/* Add similar fields for option_one, option_two, and option_three */}

        <button type="submit" className="btn btn-info">Submit</button>
      </form>
    </div>
  );
}

export default Create;
