import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Vote() {
  const [selectedOption, setSelectedOption] = useState('');
  const [email, setEmail] = useState('');
  const [pollDetails, setPollDetails] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const pollId = window.location.pathname.split('/').pop();

    // Fetch poll details from Django backend
    axios.get(`http://127.0.0.1:8000/api/polls/${pollId}/`)
      .then(response => setPollDetails(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const pollId = window.location.pathname.split('/').pop();

    // Send vote and email to Django backend
    axios.post(`http://127.0.0.1:8000/api/polls/${pollId}/vote/`, { selectedOption, email })
      .then(response => {
        console.log(response.data);
        // Redirect to results or handle success
        navigate(`/results/${pollId}`);
      })
      .catch(error => console.error(error));
  };

  if (!pollDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Cast Your Vote</h2>
      <h3>{pollDetails.question}</h3>
      <form onSubmit={handleSubmit}>
        <p>Select an option:</p>
        <div className="radio">
          {pollDetails.options && pollDetails.options.length > 0 ? (
            pollDetails.options.map((option, index) => (
              <div key={index}>
                <label>
                  <input
                    type="radio"
                    name="poll"
                    value={option}
                    checked={selectedOption === option}
                    onChange={() => setSelectedOption(option)}
                  />
                  {option}
                </label>
                <br />
              </div>
            ))
          ) : (
            <p>No options available.</p>
          )}
        </div>

        {/* Add email input field */}
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="form-control"
        />

        <button type="submit" className="btn btn-info">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Vote;
