// src/components/Vote.js
import React, { useState } from 'react';
import axios from 'axios';

function Vote({ match }) {
  const [selectedOption, setSelectedOption] = useState('');
  const [email, setEmail] = useState('');  // Add email state
  const pollId = match.params.pollId;

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send vote and email to Django backend
    axios.post(`http://127.0.0.1:8000/api/polls/${pollId}/vote/`, { selectedOption, email })
      .then(response => {
        console.log(response.data);
        // Redirect to results or handle success
      })
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h2>Cast Your Vote</h2>
      <form onSubmit={handleSubmit}>
        {/* Add email input field */}
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="form-control"
        />

        <div className="radio">
          <label>
            <input
              type="radio"
              name="poll"
              value="option1"
              checked={selectedOption === 'option1'}
              onChange={() => setSelectedOption('option1')}
            />
            Option 1
          </label>
          {/* Similar radio inputs for option2 and option3 */}
        </div>

        <button type="submit" className="btn btn-info">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Vote;
